mapper.select:
SELECT , (select count(bookISBN) from rent where userID=user.userID) as rentCount FROM user WHERE 1=1
AND userType=#{selectType}
AND userJoinDate BETWEEN #{startDate} AND #{endDate}
AND userID LIKE CONCAT('%',#{searchText},'%')
AND userName LIKE CONCAT('%',#{searchText},'%')
ORDER BY userName ASC

mapper.update:
UPDATE user 
SET  userNum = #{userNum}
	,userEmail = #{userEmail}
	,userName = #{userName}

	,userPostNum = #{userPostNum}


	,userAddr = #{userAddr}	


	,userAddrDetail = #{userAddrDetail}

WHERE userID = #{userID}

UPDATE user
SET userPW = md5('#{userPW}')
WHERE userID = #{userID}
