mapper.update:
UPDATE user 
SET  userNum = #{userNum}
	,userEmail = #{userEmail}
	,userEmailDomain = #{userEmailDomain}
	,userName = #{userName}

	,userPostNum = #{userPostNum}


	,userAddr = #{userAddr}	


	,userAddrDetail = #{userAddrDetail}

WHERE userID = #{userID}

UPDATE user
SET userPW = md5('#{userPW}')
WHERE userID = #{userID}
