mapper.select:
SELECT 
FROM user
WHERE 1=1

AND	userType=#{userType}


AND userJoinDate BETWEEN #{startDate} AND #{endDate}

ORDER BY userName ASC

SELECT FROM user WHERE 1=1
AND #{searchType}
LIKE CONCAT('%',#{searchText},'%')

mapper.update:
UPDATE user 
SET  userNum = #{userNum}
	,userEmail = #{userEmail}
	,userEmailDomain = #{userEmailDomain}
	,userName = #{userName}

	,userPostNum = #{userPostNum}


	,userAddr = #{userAddr}	


	,userAddrDetail = #{userAddrDetail}

WHERE userID = #{userID}

UPDATE user
SET userPW = md5('#{userPW}')
WHERE userID = #{userID}
